using Microsoft.AspNetCore.Razor.TagHelpers;
using Roomy.Models;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;

namespace Roomy.Helpers
{
    public class ImageTagHelper : TagHelper
    {
        public File File { get; set; }
        public override void Process(TagHelperContext context, TagHelperOutput output)
        {
            if (File.ContentType.Contains("image"))
            {
                output.TagName = "img";
                var base64 = Convert.ToBase64String(File.Content);
                var src = $"data:{File.ContentType};base64,{base64}";
                output.Attributes.SetAttribute("src", src);
                output.Attributes.SetAttribute("alt", File.Name);
            }
            else
            {
                output.TagName = "a";
                output.Attributes.SetAttribute("href", File.Name);

                output.Content.SetHtmlContent(
                    @"<img src='data:image/jpeg;base64,' />");
            }   
        }
    }
}
